import React from 'react';

const Sobre = () => (
  <section id="sobre" className="py-5">
    <div className="container">
      <h2 className="mb-4">Sobre Mim</h2>
      <p>
        Sou apaixonado por tecnologia e design. Tenho experiência com desenvolvimento web, especialmente com React, e gosto de criar interfaces intuitivas e responsivas.
      </p>
      <p>
        Busco constantemente aprender novas ferramentas e melhorar minhas habilidades para entregar soluções criativas e eficientes.
      </p>
    </div>
  </section>
);

export default Sobre;
